def load_data(name):
    attribute_list = []
    for x, y in zip(_default_data(), _difference_data(name)):
        # 優雅的合併更新字典，需要python3.5
        attribute_list.append({**x, **y})
    keys = ['floors', 'maps', 'enemies', 'player', 'items', 'npcs', 'icons']
    env_data = dict(zip(keys, attribute_list))
    return env_data


